import React, { useEffect, useState } from 'react';
import { Table, Button } from 'reactstrap';
import { getDocumentsByUser, downloadDocument } from './documentService';

const DocumentList = ({ userId, refresh }) => {
    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const response = await getDocumentsByUser(userId);
                setDocuments(response.data);
            } catch (error) {
                console.error('Error fetching documents', error);
            }
        };

        fetchDocuments();
    }, [userId, refresh]); // Refetch documents when `refresh` changes

    const handleDownload = async (documentId) => {
        try {
            const response = await downloadDocument(documentId);
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `document_${documentId}`);
            document.body.appendChild(link);
            link.click();
        } catch (error) {
            console.error('Error downloading document', error);
        }
    };

    return (
        <Table className="align-items-center table-flush" responsive>
            <thead className="thead-light">
            <tr>
                <th scope="col">ID</th>
                <th scope="col">Name</th>
                <th scope="col">Description</th>
                <th scope="col">Actions</th>
            </tr>
            </thead>
            <tbody>
            {documents.map(doc => (
                <tr key={doc.id}>
                    <td>{doc.id}</td>
                    <td>{doc.filename}</td>
                    <td>{doc.description}</td>
                    <td>
                        <Button color="primary" onClick={() => handleDownload(doc.id)}>
                            Download
                        </Button>
                    </td>
                </tr>
            ))}
            </tbody>
        </Table>
    );
};

export default DocumentList;
