import React, { useState, useEffect } from "react";
import {
  Button,
  Card,
  CardHeader,
  CardBody,
  FormGroup,
  Form,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col,
} from "reactstrap";
import DemoNavbar from "components/Navbars/DemoNavbar.js";
import SimpleFooter from "components/Footers/SimpleFooter.js";
import axios from "axios";

const AddProfile = () => {
  const [nom, setNom] = useState("");
  const [prenom, setPrenom] = useState("");
  const [pole, setPole] = useState("");
  const [domaine, setDomaine] = useState("");
  const [metier, setMetier] = useState("");
  const [filiere, setFiliere] = useState("");
  const [lieuDeTravail, setLieuDeTravail] = useState("");
  const [responsable, setResponsable] = useState("");
  const [email, setEmail] = useState("");
  const [civilite, setCivilite] = useState("");
  const [sexe, setSexe] = useState("");
  const [nationalite, setNationalite] = useState("");
  const [dateEtLieuDeNaissance, setDateEtLieuDeNaissance] = useState("");
  const [adresseDomicile, setAdresseDomicile] = useState("");
  const [image, setImage] = useState(null);
  const [selectedEmployeeId, setSelectedEmployeeId] = useState("");

  const [message, setMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [isSubmit, setIsSubmit] = useState(false);
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get("http://localhost:3000/users");
        setEmployees(response.data);
      } catch (error) {
        console.error("Error fetching managers:", error);
      }
    };
    fetchEmployees();
  }, []);

  const handleInputChange = async (e) => {
    const { name, value, files } = e.target;

    if (name === "employeeId") {
      setSelectedEmployeeId(value);
      if (value) {
        try {
          const user = await axios.get(`http://localhost:3000/users/${value}`);
          console.log(user.data.profile.id)
          const response = await axios.get(`http://localhost:3000/profile/${user.data.profile.id}`);
          const profile = response.data;
          user.profile = profile;
          const employee = JSON.stringify(user.data);
         
          localStorage.setItem("user",  employee)

          setNom(profile.nom || "");
          setPrenom(profile.prenom || "");
          setPole(profile.pole || "");
          setDomaine(profile.domaine || "");
          setMetier(profile.metier || "");
          setFiliere(profile.filiere || "");
          setLieuDeTravail(profile.lieuDeTravail || "");
          setResponsable(profile.responsable || "");
          setEmail(profile.email || "");
          setCivilite(profile.civilite || "");
          setSexe(profile.sexe || "");
          setNationalite(profile.nationalite || "");
          setDateEtLieuDeNaissance(profile.dateEtLieuDeNaissance || "");
          setAdresseDomicile(profile.adresseDomicile || "");
          setImage(profile.image || null);
        } catch (error) {
          console.error("Error fetching profile", error);
        }
      } else {
        setNom("");
        setPrenom("");
        setPole("");
        setDomaine("");
        setMetier("");
        setFiliere("");
        setLieuDeTravail("");
        setResponsable("");
        setEmail("");
        setCivilite("");
        setSexe("");
        setNationalite("");
        setDateEtLieuDeNaissance("");
        setAdresseDomicile("");
        setImage(null);
      }
    }

    if (name === "nom") setNom(value);
    if (name === "prenom") setPrenom(value);
    if (name === "pole") setPole(value);
    if (name === "domaine") setDomaine(value);
    if (name === "metier") setMetier(value);
    if (name === "filiere") setFiliere(value);
    if (name === "lieuDeTravail") setLieuDeTravail(value);
    if (name === "responsable") setResponsable(value);
    if (name === "email") setEmail(value);
    if (name === "civilite") setCivilite(value);
    if (name === "sexe") setSexe(value);
    if (name === "nationalite") setNationalite(value);
    if (name === "dateEtLieuDeNaissance") setDateEtLieuDeNaissance(value);
    if (name === "adresseDomicile") setAdresseDomicile(value);
    if (name === "image") setImage(files[0]);
  };

  const validateForm = () => {
    // Perform any necessary validation here
    return true;
  };

  const handleAddProfile = async (e) => {
    e.preventDefault();
    if (!isSubmit) {
      setIsSubmit(true);
      if (validateForm()) {
        if (!selectedEmployeeId) {
          setErrorMessage("Please select a manager.");
          setIsSubmit(false);
          return;
        }

        const formData = new FormData();
        formData.append("nom", nom);
        formData.append("prenom", prenom);
        formData.append("pole", pole);
        formData.append("domaine", domaine);
        formData.append("metier", metier);
        formData.append("filiere", filiere);
        formData.append("lieuDeTravail", lieuDeTravail);
        formData.append("responsable", responsable);
        formData.append("email", email);
        formData.append("civilite", civilite);
        formData.append("sexe", sexe);
        formData.append("nationalite", nationalite);
        formData.append("dateEtLieuDeNaissance", dateEtLieuDeNaissance);
        formData.append("adresseDomicile", adresseDomicile);
        if (image) {
          formData.append("image", image);
        }
        try {
          console.log("id", selectedEmployeeId);
          const response = await axios.post(
            `http://localhost:3000/profile/${selectedEmployeeId}`,
            formData,
            {
              headers: {
                "Content-Type": "multipart/form-data",
              },
            }
          );
          setMessage("Profile created successfully!");
          setNom("");
          setPrenom("");
          setPole("");
          setDomaine("");
          setMetier("");
          setFiliere("");
          setLieuDeTravail("");
          setResponsable("");
          setEmail("");
          setCivilite("");
          setSexe("");
          setNationalite("");
          setDateEtLieuDeNaissance("");
          setAdresseDomicile("");
          setImage(null);
          setSelectedEmployeeId("");
          setErrorMessage("");
        } catch (error) {
          setErrorMessage("There was an error creating the profile!");
          console.error("Error creating profile", error);
        }
      } else {
        setErrorMessage("Please correct the errors in the form.");
      }
      setIsSubmit(false);
    }
  };

  return (
    <>
      <DemoNavbar />
      <main>
        <section className="section section-shaped section-lg">
          <div className="shape shape-style-1 bg-gradient-default">
            <span />
            <span />
            <span />
            <span />
            <span />
            <span />
            <span />
            <span />
          </div>
          <Container className="pt-lg-7">
            <Row className="justify-content-center">
              <Col lg="5">
                <Card className="bg-secondary shadow border-0">
                  <CardHeader className="bg-white pb-5">
                    <div className="text-muted text-center mb-3">
                      <small>Add New Profile</small>
                    </div>
                  </CardHeader>
                  <CardBody className="px-lg-5 py-lg-5">
                    <div className="text-center text-muted mb-4">
                      <small>Fill in the details below</small>
                    </div>
                    <Form role="form" onSubmit={handleAddProfile}>
                      <FormGroup>
                        <InputGroup className="input-group-alternative">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-single-02" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            type="select"
                            name="employeeId"
                            value={selectedEmployeeId}
                            onChange={handleInputChange}
                          >
                            <option value="">Select Employee</option>
                            {employees.map((manager) => (
                              <option key={manager.id} value={manager.id}>
                                {manager.username}
                              </option>
                            ))}
                          </Input>
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-single-copy-04" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Nom"
                            type="text"
                            name="nom"
                            value={nom}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-single-copy-04" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Prenom"
                            type="text"
                            name="prenom"
                            value={prenom}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-building" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Pole"
                            type="text"
                            name="pole"
                            value={pole}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-briefcase-24" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Domaine"
                            type="text"
                            name="domaine"
                            value={domaine}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-briefcase-24" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Metier"
                            type="text"
                            name="metier"
                            value={metier}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-hat-3" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Filiere"
                            type="text"
                            name="filiere"
                            value={filiere}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-pin-3" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Lieu de Travail"
                            type="text"
                            name="lieuDeTravail"
                            value={lieuDeTravail}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-single-02" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Responsable"
                            type="text"
                            name="responsable"
                            value={responsable}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-email-83" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Email"
                            type="email"
                            name="email"
                            value={email}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-circle-08" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Civilite"
                            type="text"
                            name="civilite"
                            value={civilite}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-favourite-28" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Sexe"
                            type="text"
                            name="sexe"
                            value={sexe}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-world-2" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Nationalite"
                            type="text"
                            name="nationalite"
                            value={nationalite}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-calendar-grid-58" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Date et Lieu de Naissance"
                            type="text"
                            name="dateEtLieuDeNaissance"
                            value={dateEtLieuDeNaissance}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-square-pin" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            placeholder="Adresse Domicile"
                            type="text"
                            name="adresseDomicile"
                            value={adresseDomicile}
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      <FormGroup>
                        <InputGroup className="input-group-alternative mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="ni ni-image" />
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            type="file"
                            name="image"
                            onChange={handleInputChange}
                          />
                        </InputGroup>
                      </FormGroup>
                      {message && (
                        <div className="text-center mb-3 alert alert-success">
                          <small>{message}</small>
                        </div>
                      )}
                      {errorMessage && (
                        <div className="text-center mb-3 alert alert-danger">
                          <small>{errorMessage}</small>
                        </div>
                      )}
                      <div className="text-center">
                        <Button
                          className="mt-4"
                          color="primary"
                          type="submit"
                          disabled={isSubmit}
                        >
                          Add Profile
                        </Button>
                      </div>
                    </Form>
                  </CardBody>
                </Card>
              </Col>
            </Row>
          </Container>
        </section>
      </main>
      <SimpleFooter />
    </>
  );
};

export default AddProfile;
