import React, { useEffect, useState } from 'react';
import { Table } from 'reactstrap';
import { getDocumentsByUser } from './documentService';
import DownloadButton from './DownloadButton';
import DeleteButton from './DeleteButton';

const DocumentList = ({ userId, onDocumentUpdated }) => {
    const [documents, setDocuments] = useState([]);

    useEffect(() => {
        fetchDocuments();
    }, [userId]);

    const fetchDocuments = async () => {
        try {
            const response = await getDocumentsByUser(userId);
            setDocuments(response.data);
        } catch (error) {
            console.error('There was an error fetching the documents!', error);
        }
    };

    const handleDelete = () => {
        fetchDocuments(); // Refresh the document list after deletion
        if (onDocumentUpdated) onDocumentUpdated(); // Notify parent component if needed
    };

    return (
        <Table className="align-items-center table-flush" responsive>
            <thead className="thead-light">
            <tr>
                <th scope="col">Document Name</th>
                <th scope="col">Description</th>
                <th scope="col">Download Document</th>
                <th scope="col">Delete Document</th>
            </tr>
            </thead>
            <tbody>
            {documents.map(doc => (
                <tr key={doc.id}>
                    <td>{doc.filename}</td>
                    <td>
                        <div className="text-truncate" style={{ maxWidth: '150px' }}>
                            {doc.description}
                        </div>
                    </td>
                    <td>
                        <DownloadButton documentId={doc.id} />
                    </td>
                    <td>
                        <DeleteButton documentId={doc.id} onDelete={handleDelete} />
                    </td>
                </tr>
            ))}
            </tbody>
        </Table>
    );
};

export default DocumentList;
