import React from 'react';
import { Input, Button } from 'reactstrap';
import { FaEdit, FaTrash } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const UserTableRow = ({ user, roles, managers, onInputChange, onConfirm, onDelete }) => {
    return (
        <tr key={user.id}>
            <td>{user.id}</td>
            <td><Input type="text" name="username" defaultValue={user.username} onChange={(e) => onInputChange(e, user.id)} /></td>
            <td><Input type="email" name="email" defaultValue={user.email} onChange={(e) => onInputChange(e, user.id)} /></td>
            <td><Input type="password" name="password" placeholder="Enter new password" onChange={(e) => onInputChange(e, user.id)} /></td>
            <td>
                <Input type="select" name="role" value={user.role?.id || ''} onChange={(e) => onInputChange(e, user.id)}>
                    <option value="">Select Role</option>
                    {roles.map(role => (
                        <option key={role.id} value={role.id}>{role.name}</option>
                    ))}
                </Input>
            </td>
            <td>
                <Input type="select" name="manager" value={user.manager?.id || ''} onChange={(e) => onInputChange(e, user.id)}>
                    <option value="">Select Manager</option>
                    {managers.map(manager => (
                        <option key={manager.id} value={manager.id}>{manager.username}</option>
                    ))}
                </Input>
            </td>
            <td><Link to={`/documents/${user.id}`}><Button color="info" size="sm">Manage Documents</Button></Link></td>
            <td><Button color="primary" size="sm" onClick={() => onConfirm(user.id)}><FaEdit /> Edit</Button></td>
            <td><Button color="danger" size="sm" onClick={() => onDelete(user.id)}><FaTrash /> Delete</Button></td>
        </tr>
    );
};

export default UserTableRow;
