import React, { useEffect, useState, useCallback } from 'react';
import { Table } from 'reactstrap';
import { getDocumentsByUser, deleteDocument } from './documentService';
import DownloadButton from './DownloadButton';

const DocumentList = ({ userId }) => {
    const [documents, setDocuments] = useState([]);

    // Define fetchDocuments as a stable function using useCallback
    const fetchDocuments = useCallback(() => {
        getDocumentsByUser(userId)
            .then(response => {
                setDocuments(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the documents!', error);
            });
    }, [userId]); // Dependency array includes userId

    useEffect(() => {
        fetchDocuments(); // Fetch documents when userId changes
    }, [fetchDocuments]); // Dependency array includes fetchDocuments

    const handleDelete = async (documentId) => {
        try {
            await deleteDocument(documentId);
            // Refresh the document list after deletion
            fetchDocuments();
        } catch (error) {
            console.error('Error deleting document:', error.response ? error.response.data : error.message);
            alert('Failed to delete document');
        }
    };

    return (
        <Table className="align-items-center table-flush" responsive>
            <thead className="thead-light">
            <tr>
                <th scope="col">Document Name</th>
                <th scope="col">Description</th>
                <th scope="col">Download Document</th>
                <th scope="col">Delete Document</th>
            </tr>
            </thead>
            <tbody>
            {documents.map(doc => (
                <tr key={doc.id}>
                    <td>{doc.filename}</td>
                    <td className="text-truncate" style={{ maxWidth: '200px' }}>
                        {doc.description}
                    </td>
                    <td>
                        <DownloadButton documentId={doc.id} />
                    </td>
                    <td>
                        <Button color="danger" size="sm" onClick={() => handleDelete(doc.id)}>
                            Delete
                        </Button>
                    </td>
                </tr>
            ))}
            </tbody>
        </Table>
    );
};

export default DocumentList;
