import React, { useEffect, useState } from "react";
import "./Auth.css";
import axios from "axios";
import { Link, Navigate } from "react-router-dom";
import toastr from "toastr";
import "toastr/build/toastr.min.css";

const SignInSignUp = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [profileImage, setProfileImage] = useState(null);
  const [profileImageName, setProfileImageName] = useState("");

  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [firstNameError, setFirstNameError] = useState("");
  const [lastNameError, setLastNameError] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");
  const [touchedFields, setTouchedFields] = useState({});
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [redirect, setRedirect] = useState(false);
  const [shouldNavigate, setShouldNavigate] = useState(false);
  const [isSubmit, setIsSubmit] = useState(false);

  const togglePasswordVisibility = () => {
    setShowPassword((prevState) => !prevState);
  };

  const handleFieldBlur = (field) => {
    setTouchedFields({ ...touchedFields, [field]: true });
    if (field === "email") {
      validateEmail();
    } else if (field === "password") {
      validatePassword();
    } else if (field === "firstName") {
      validateFirstName();
    } else if (field === "lastName") {
      validateLastName();
    } else if (field === "confirmPassword") {
      validateConfirmPassword();
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      const userDataFromStorage = localStorage.getItem("userData");
      const parsedUserData = JSON.parse(userDataFromStorage);

      if (parsedUserData) {
        console.log("gggggggggggggggggggg");
        console.log(parsedUserData);
        setShouldNavigate(true);
      }
    }
  }, [isLoggedIn]);

  const validateEmail = () => {
    if (!/\S+@\S+\.\S+/.test(email)) {
      setEmailError("Please enter a valid email address.");
    } else {
      setEmailError("");
    }
  };

  const validatePassword = () => {
    if (!password) {
      setPasswordError("Password is required.");
    } else {
      setPasswordError("");
    }
  };

  const validateFirstName = () => {
    if (!firstName) {
      setFirstNameError("First name is required.");
    } else {
      setFirstNameError("");
    }
  };
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
      setProfileImageName(file.name);
    } else {
      setProfileImage(null);
      setProfileImageName("");
    }
  };

  const validateLastName = () => {
    if (!lastName) {
      setLastNameError("Last name is required.");
    } else {
      setLastNameError("");
    }
  };

  const validateConfirmPassword = () => {
    if (!confirmPassword) {
      setConfirmPasswordError("Confirm password is required.");
    } else if (confirmPassword !== password) {
      setConfirmPasswordError("Passwords do not match.");
    } else {
      setConfirmPasswordError("");
    }
  };

  const validateForm = () => {
    setIsSubmit(false); // Reset isSubmit state

    validateEmail();
    validatePassword();
    validateFirstName();
    validateLastName();
    validateConfirmPassword();

    return (
      !emailError &&
      !passwordError &&
      !firstNameError &&
      !lastNameError &&
      !confirmPasswordError
    );
  };

  const handleSignIn = async (e) => {
    e.preventDefault();
    if (!isSubmit) {
      setIsSubmit(true);
      try {
        if (validateForm()) {
          const response = await axios.post(
            "http://localhost:3000/auth/signin",
            {
              email: email,
              password: password,
            },
            { withCredentials: true }
          );

          console.log("Server Response:", response);

          const { data } = response;

          if (data && data.access_token) {
            axios.defaults.headers.common[
              "Authorization"
            ] = `Bearer ${data.access_token}`;

            console.log("Access Token:", data.access_token);

            setIsLoggedIn(true);
            setRedirect(true);

            try {
              const { data } = await axios.get(
                "http://localhost:3000/auth/user"
              );
              console.log("new data");
              console.log(data);

              localStorage.setItem("userData", JSON.stringify(data));
              setShouldNavigate(true);
            } catch (error) {
              console.error("Error fetching user data:", error);
            }
          } else {
            setIsSubmit(false); // Reset isSubmit state

            console.error("Access token not found in server response");
            toastr.error("Incorrect email or password", null, {
              closeButton: true,
              debug: false,
              newestOnTop: true,
              progressBar: true,
              positionClass: "toast-top-right",
              preventDuplicates: false,
              onclick: null,
              showDuration: "300",
              hideDuration: "1000",
              timeOut: "5000",
              extendedTimeOut: "1000",
              showEasing: "swing",
              hideEasing: "linear",
              showMethod: "fadeIn",
              hideMethod: "fadeOut",
              backgroundcolor: "purple",
            });
            setEmail("");
            setPassword("");
            setFirstName("");
            setLastName("");
            setConfirmPassword("");
            setEmailError("");
            setPasswordError("");
            setFirstNameError("");
            setLastNameError("");
            setConfirmPasswordError("");
          }
        }
        setIsSubmit(false); // Reset isSubmit state
      } catch (error) {
        console.error("Login error:", error.message);
      }
      setIsSubmit(false);
    }
  };

  const handleSignUp = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const formData = new FormData();
        formData.append("firstname", firstName);
        formData.append("lastname", lastName);
        formData.append("email", email);
        formData.append("password", password);
        formData.append("confirm_password", confirmPassword);
        if (profileImage) {
          formData.append("file", profileImage);
          console.log(profileImage, " image appended"); // Append the image file
        }

        const response = await axios.post(
          "http://localhost:3000/auth/signup",
          formData,
          {
            headers: {
              "Content-Type": "multipart/form-data",
            },
          }
        );

        console.log("Server Response:", response);
        toastr.success("Sign up successful", null, {
          closeButton: true,
          debug: false,
          newestOnTop: true,
          progressBar: true,
          positionClass: "toast-top-right",
          preventDuplicates: false,
          onclick: null,
          showDuration: "300",
          hideDuration: "1000",
          timeOut: "5000",
          extendedTimeOut: "1000",
          showEasing: "swing",
          hideEasing: "linear",
          showMethod: "fadeIn",
          hideMethod: "fadeOut",
          backgroundcolor: "green",
        });
        setFirstName("");
        setLastName("");
        setEmail("");
        setPassword("");
        setConfirmPassword("");
        setProfileImage(null); // Reset profileImage state
      } catch (error) {
        console.error("Signup error:", error.message);
        toastr.error("Signup failed", null, {
          closeButton: true,
          debug: false,
          newestOnTop: true,
          progressBar: true,
          positionClass: "toast-top-right",
          preventDuplicates: false,
          onclick: null,
          showDuration: "300",
          hideDuration: "1000",
          timeOut: "5000",
          extendedTimeOut: "1000",
          showEasing: "swing",
          hideEasing: "linear",
          showMethod: "fadeIn",
          hideMethod: "fadeOut",
          backgroundcolor: "red",
        });
      }
    }
  };

  return (
    <div className="body">
      {shouldNavigate && <Navigate to="/" />}
      <div className="cont">
        <input type="checkbox" id="flip" />
        <div className="cover">
          <div className="front">
            <img src="./public/Documents (1).gif" alt="" />
            <div className="text">
              <span className="text-1">
                Every new friend is a <br /> new adventure
              </span>
              <span className="text-2">Let's get connected</span>
            </div>
          </div>
        </div>
        <div className="forms">
          <div className="form-content">
            <div className="login-form">
              <div className="title">Login</div>
              <form>
                <div className="input-boxes">
                  <div className={`input-box ${emailError ? "error" : ""}`}>
                    <i className="fas fa-envelope"></i>
                    <input
                      type="text"
                      placeholder="Enter your email"
                      value={email}
                      onBlur={() => handleFieldBlur("email")}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>
                  {emailError && (
                    <span className="error-message">{emailError}</span>
                  )}

                  <div className={`input-box ${passwordError ? "error" : ""}`}>
                    <i className="fas fa-lock"></i>
                    <input
                      type={showPassword ? "text" : "password"}
                      placeholder="Enter your password"
                      value={password}
                      onBlur={() => handleFieldBlur("password")}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                    <button type="button" onClick={togglePasswordVisibility}>
                      <i
                        className={`fas ${
                          showPassword ? "fa-eye-slash" : "fa-eye"
                        }`}
                        style={{ cursor: "pointer", border: "none" }}
                      ></i>
                    </button>
                  </div>

                  {passwordError && (
                    <span className="error-message">{passwordError}</span>
                  )}

                  <div className="button input-box">
                    <input
                      onClick={handleSignIn}
                      type="submit"
                      value="Submit"
                      disabled={isSubmit} // Disable the button while the form is being submitted
                    />
                  </div>
                  <div className="text sign-up-text">
                    <Link to="/forgot">RESET YOUR PASSWORD</Link>
                  </div>
                  <div className="text sign-up-text">
                    Don't have an account?{" "}
                    <label htmlFor="flip">Signup now</label>
                  </div>
                </div>
              </form>
            </div>
            <div className="signup-form">
              <div className="title">Signup</div>
              <form onSubmit={handleSignUp}>
                <div className="input-boxes">
                  <div
                    className={`input-box ${
                      touchedFields.firstName && firstNameError ? "error" : ""
                    }`}
                  >
                    <i className="fas fa-user"></i>
                    <input
                      type="text"
                      placeholder="Enter your firstname"
                      value={firstName}
                      onChange={(e) => setFirstName(e.target.value)}
                      onBlur={() => handleFieldBlur("firstName")}
                    />
                  </div>
                  {touchedFields.firstName && firstNameError && (
                    <span className="error-message">{firstNameError}</span>
                  )}

                  <div
                    className={`input-box ${
                      touchedFields.lastName && lastNameError ? "error" : ""
                    }`}
                  >
                    <i className="fas fa-user"></i>
                    <input
                      type="text"
                      placeholder="Enter your lastname"
                      value={lastName}
                      onBlur={() => handleFieldBlur("lastName")}
                      onChange={(e) => setLastName(e.target.value)}
                    />
                  </div>
                  {touchedFields.lastName && lastNameError && (
                    <span className="error-message">{lastNameError}</span>
                  )}

                  <div
                    className={`input-box ${
                      touchedFields.email && emailError ? "error" : ""
                    }`}
                  >
                    <i className="fas fa-envelope"></i>
                    <input
                      type="text"
                      placeholder="Enter your email"
                      value={email}
                      onBlur={() => handleFieldBlur("email")}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>

                  {touchedFields.email && emailError && (
                    <span className="error-message">{emailError}</span>
                  )}

                  <div
                    className={`input-box ${
                      touchedFields.password && passwordError ? "error" : ""
                    }`}
                  >
                    <i className="fas fa-lock"></i>
                    <input
                      type={showPassword ? "text" : "password"}
                      placeholder="Enter your password"
                      value={password}
                      onBlur={() => handleFieldBlur("password")}
                      onChange={(e) => setPassword(e.target.value)}
                    />
                    <button
                      type="button"
                      onClick={() => togglePasswordVisibility("password")}
                    >
                      <i
                        className={`fas ${
                          showPassword ? "fa-eye-slash" : "fa-eye"
                        }`}
                        style={{ cursor: "pointer", border: "none" }}
                      ></i>
                    </button>
                  </div>

                  {touchedFields.password && passwordError && (
                    <span className="error-message">{passwordError}</span>
                  )}

                  <div
                    className={`input-box ${
                      touchedFields.confirmPassword && confirmPasswordError
                        ? "error"
                        : ""
                    }`}
                  >
                    <i className="fas fa-lock"></i>
                    <input
                      type={showPassword ? "text" : "password"}
                      placeholder="Confirm your password"
                      value={confirmPassword}
                      onBlur={() => handleFieldBlur("confirmPassword")}
                      onChange={(e) => setConfirmPassword(e.target.value)}
                    />
                    <br />
                    <br />

                    <button
                      type="button"
                      onClick={() =>
                        togglePasswordVisibility("confirmPassword")
                      }
                    >
                      <i
                        className={`fas ${
                          showPassword ? "fa-eye-slash" : "fa-eye"
                        }`}
                        style={{ cursor: "pointer", border: "none" }}
                      ></i>
                    </button>
                  </div>

                  {touchedFields.confirmPassword && confirmPasswordError && (
                    <span className="error-message">
                      {confirmPasswordError}
                    </span>
                  )}

                  <div className="input-box">
                    <i className="fas fa-upload"></i>
                    <input
                      type="file"
                      name="profileImage"
                      onChange={handleImageChange}
                    />
                    {profileImageName && (
                      <div className="file-name">{profileImageName}</div>
                    )}
                  </div>

                  <div className="button input-box">
                    <input type="submit" value="Submit" disabled={isSubmit} />
                  </div>
                  <div className="text sign-up-text">
                    Already have an account?{" "}
                    <label htmlFor="flip">Login now</label>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SignInSignUp;
