import React, { useState } from 'react';
import { Container, Row, Col, Card, CardBody } from 'reactstrap';
import DemoNavbar from 'components/Navbars/DemoNavbar';
import SimpleFooter from 'components/Footers/SimpleFooter';
import DocumentList from 'components/DocumentManagement/DocumentList';
import UploadDocument from 'components/DocumentManagement/UploadDocument';

const DocumentManagementPage = ({ userId }) => {
    const [refresh, setRefresh] = useState(false);

    const handleRefreshDocuments = () => {
        setRefresh(!refresh);
    };

    return (
        <main>
            <DemoNavbar />
            <section className="section section-shaped section-lg">
                <div className="shape shape-style-1 bg-gradient-default">
                    <span />
                    <span />
                    <span />
                    <span />
                    <span />
                    <span />
                    <span />
                    <span />
                </div>
                <Container className="pt-lg-7">
                    <Row className="justify-content-center">
                        <Col lg="12">
                            <Card className="shadow border-0">
                                <CardBody className="px-lg-5 py-lg-5">
                                    <div className="text-center text-muted mb-4">
                                        <h2>Document Management</h2>
                                    </div>
                                    {/* DocumentList placed above UploadDocument */}
                                    <DocumentList userId={userId} refresh={refresh} />
                                    <div className="mt-5">
                                        <UploadDocument userId={userId} refreshDocuments={handleRefreshDocuments} />
                                    </div>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </section>
            <SimpleFooter />
        </main>
    );
};

export default DocumentManagementPage;
