import React, { useState, useEffect } from "react";


import axios from "axios";
import DemoNavbar from "components/Navbars/DemoNavbar";

const ProfileComponent = () => {
  const [userData, setUserData] = useState({
    email: "",
    firstName: "",
    lastName: "",
  });
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const userId = "your-user-id"; // Replace with the actual user ID logic

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/users/${userId}`);
        setUserData(response.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, [userId]);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setUserData({ ...userData, [name]: value });
  };

  const handlePasswordChange = (event) => {
    const { name, value } = event.target;
    if (name === "oldPassword") setOldPassword(value);
    if (name === "newPassword") setNewPassword(value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      await axios.put(`/users/${userId}`, userData);
      alert("Profile updated successfully");
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Error updating profile");
    }
  };

  const handleUpdatePassword = async (event) => {
    event.preventDefault();

    const passwordData = {
      oldPassword,
      newPassword,
    };

    try {
      await axios.put(`/users/${userId}/password`, passwordData);
      alert("Password updated successfully");
    } catch (error) {
      console.error("Error updating password:", error);
      alert("Error updating password");
    }
  };

  return (
    <>

      <DemoNavbar />
      <main id="main">
        <section className="section profile">
          <div className="row">
            <div className="col-xl-4">
              <div className="card">
                <div className="card-body profile-card pt-4 d-flex flex-column align-items-center">
                  <img
                    src="assets/img/profile-img.jpg"
                    alt="Profile"
                    className="rounded-circle"
                  />
                  <h2>{userData.firstName + " " + userData.lastName}</h2>
                  <h3>{userData.email}</h3>
                  <div className="social-links mt-2">
                    <a href="#" className="twitter">
                      <i className="bi bi-twitter"></i>
                    </a>
                    <a href="#" className="facebook">
                      <i className="bi bi-facebook"></i>
                    </a>
                    <a href="#" className="instagram">
                      <i className="bi bi-instagram"></i>
                    </a>
                    <a href="#" className="linkedin">
                      <i className="bi bi-linkedin"></i>
                    </a>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-xl-8">
              <div className="card">
                <div className="card-body pt-3">
                  <ul className="nav nav-tabs nav-tabs-bordered">
                    <li className="nav-item">
                      <button
                        className="nav-link active"
                        data-bs-toggle="tab"
                        data-bs-target="#profile-overview"
                      >
                        Overview
                      </button>
                    </li>
                    <li className="nav-item">
                      <button
                        className="nav-link"
                        data-bs-toggle="tab"
                        data-bs-target="#profile-edit"
                      >
                        Edit Profile
                      </button>
                    </li>
                    <li className="nav-item">
                      <button
                        className="nav-link"
                        data-bs-toggle="tab"
                        data-bs-target="#profile-change-password"
                      >
                        Change Password
                      </button>
                    </li>
                  </ul>
                  <div className="tab-content pt-2">
                    <div
                      className="tab-pane fade show active profile-overview"
                      id="profile-overview"
                    >
                      <h5 className="card-title">Profile Details</h5>
                      <div className="row">
                        <div className="col-lg-3 col-md-4 label">First Name</div>
                        <div className="col-lg-9 col-md-8">{userData.firstName}</div>
                      </div>
                      <div className="row">
                        <div className="col-lg-3 col-md-4 label">Last Name</div>
                        <div className="col-lg-9 col-md-8">{userData.lastName}</div>
                      </div>
                      <div className="row">
                        <div className="col-lg-3 col-md-4 label">Email</div>
                        <div className="col-lg-9 col-md-8">{userData.email}</div>
                      </div>
                    </div>
                    <div
                      className="tab-pane fade profile-edit pt-3"
                      id="profile-edit"
                    >
                      <form onSubmit={handleSubmit}>
                        <div className="row mb-3">
                          <div className="col-lg-3 col-md-4 label">First Name</div>
                          <div className="col-lg-9 col-md-8">
                            <input
                              name="firstName"
                              type="text"
                              className="form-control"
                              value={userData.firstName}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        <div className="row mb-3">
                          <div className="col-lg-3 col-md-4 label">Last Name</div>
                          <div className="col-lg-9 col-md-8">
                            <input
                              name="lastName"
                              type="text"
                              className="form-control"
                              value={userData.lastName}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        <div className="row mb-3">
                          <div className="col-lg-3 col-md-4 label">Email</div>
                          <div className="col-lg-9 col-md-8">
                            <input
                              name="email"
                              type="email"
                              className="form-control"
                              value={userData.email}
                              onChange={handleInputChange}
                            />
                          </div>
                        </div>
                        <div className="text-center">
                          <button type="submit" className="btn btn-primary">
                            Save Changes
                          </button>
                        </div>
                      </form>
                    </div>
                    <div
                      className="tab-pane fade pt-3"
                      id="profile-change-password"
                    >
                      <form onSubmit={handleUpdatePassword}>
                        <div className="row mb-3">
                          <div className="col-lg-3 col-md-4 label">Old Password</div>
                          <div className="col-lg-9 col-md-8">
                            <input
                              name="oldPassword"
                              type="password"
                              className="form-control"
                              value={oldPassword}
                              onChange={handlePasswordChange}
                            />
                          </div>
                        </div>
                        <div className="row mb-3">
                          <div className="col-lg-3 col-md-4 label">New Password</div>
                          <div className="col-lg-9 col-md-8">
                            <input
                              name="newPassword"
                              type="password"
                              className="form-control"
                              value={newPassword}
                              onChange={handlePasswordChange}
                            />
                          </div>
                        </div>
                        <div className="text-center">
                          <button type="submit" className="btn btn-primary">
                            Change Password
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    </>
  );
};

export default ProfileComponent;
