import axios from 'axios';

const API_BASE_URL = 'http://your-backend-url/documents';

const uploadDocument = (userId, file, filename) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('filename', filename);

    return axios.post(`${API_BASE_URL}/upload/${userId}`, formData, {
        headers: {
            'Content-Type': 'multipart/form-data'
        }
    });
};

const getDocumentsByUser = (userId) => {
    return axios.get(`${API_BASE_URL}/user/${userId}`);
};

const downloadDocument = (documentId) => {
    return axios.get(`${API_BASE_URL}/download/${documentId}`, {
        responseType: 'blob'
    });
};

export default {
    uploadDocument,
    getDocumentsByUser,
    downloadDocument
};
